import {useState} from 'react'
import {Input} from 'semantic-ui-react'

function Login(){
  const [userName, setUserName] = useState("")
  const [userPassword, setUserPassword] = useState("")

  function whatUserNamed(e){
    setUserName(e.target.value)
  }

  function whatUserPassworded(e){
    setUserPassword(e.target.value)
  }

  function handleClick(e){
    e.preventDefault()
    fetch(`http://localhost:3000/login`, {
      method: "POST",
      headers:{
        "Content-Type": "application/json"
      },
      body: JSON.stringify({name: whatUserNamed, password: whatUserPassworded})
    })
     .then(res => res.json())
     .then(userInfo =>
      localStorage.token = userInfo.token)
  }

  return (
    <div>
      <form onSubmit={handleClick}>
        <Input className="input" label='Name' id="name" value={userName} onChange={whatUserNamed} type="text" placeholder="Your name"></Input>
        <Input className="input" label='Password' id="userpassword" value={userPassword} onChange={whatUserPassworded} type="url" placeholder="Your password"></Input>
        <button class="ui button">Submit new beer</button>
      </form>
    </div>
  )
}

export default Login
